import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function NavBar() {
  const location = useLocation();
  const hideButtonsOnPaths = ['/signin', '/signup', '/reset-password', '/forgotten_password'];

  const shouldShowButtons = !hideButtonsOnPaths.includes(location.pathname);

  return (
    <header className="App-header">
      <a href="/">swiftEntry</a>
      {shouldShowButtons && <Link to="/signin" className="link-style">Sign In</Link>}
      {shouldShowButtons && <Link to="/signup" className="link-style">Create Account</Link>}
    </header>
  );
}

export default NavBar;
